import Carousel from "react-multi-carousel"
import { useState, useEffect } from "react";
import Card from "@mui/material/Card";
import CardMedia from "@mui/material/CardMedia";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import AddShoppingCartIcon from "@mui/icons-material/AddShoppingCart";
import { Paper } from "@mui/material";
import Fab from '@mui/material/Fab';
import Box from '@mui/material/Box';
import Router from "next/router";
import axios from "axios";

const responsive = {
    superLargeDesktop: {
        // the naming can be any, depends on you.
        breakpoint: { max: 4000, min: 3000 },
        items: 4,
    },
    desktop: {
        breakpoint: { max: 3000, min: 1024 },
        items: 4,
    },
    tablet: {
        breakpoint: { max: 1024, min: 464 },
        items: 2,
    },
    mobile: {
        breakpoint: { max: 464, min: 0 },
        items: 2,
    },
};

const Productcart = () => {
    const v = [1, 2, 3, 4, 5, 6, 7, 8, 9]
    const [cartimg, setCartimg] = useState(v);

    const setaddindividualpage = () => {
        Router.push('/individualpage');
    }

    

    return (
        <div className='container'>
            <div className="col-12">
                <div>
                    <h4 className="text-center "><strong>Trending Products</strong></h4>
                </div>
                <Carousel
                    responsive={responsive}
                    autoPlay={true}
                    autoPlaySpeed={2000}
                    infinite={true}
                    removeArrowOnDeviceType={"mobile"}


                >

                    {
                        cartimg.map((item) => {
                            return (
                                <Card className=" mx-2 " key={item}>

                                    <div className='main-productimg'>
                                        <div
                                            className=" d-flex flex-column position-absolute"
                                            style={{ top: "10px", right: "0px", zIndex: "20" }}
                                        >
                                            <span className="newproducrt-icon me-3">
                                                <AddShoppingCartIcon className="icon2-header " />
                                            </span>
                                            <span className="mt-2 newproducrt-icon">
                                                <FavoriteBorderIcon className="icon2-header" />
                                            </span>
                                        </div>
                                        <CardMedia
                                            component="img"
                                            image={item}
                                            alt="green iguana"
                                            className='productcart-img '
                                            style={{ width: '100%', height: 'auto' }}
                                        />
                                    </div>
                                   
                                </Card>


                            )
                        })
                    }
                </Carousel>







            </div>
        </div>
    )
}
export default Productcart
